import React from "react";
import { Alert } from "react-bootstrap";

interface IProps {
  variant: string,
  children: any
};

const Message: React.FC<IProps> = ({ variant, children }) => {
  return <Alert variant={variant}>{children}</Alert>;
};

Message.defaultProps = {
  variant: "info"
}

export default Message;
